<script>
import { mapGetters } from "vuex";
import lang from "~/helper/lang.json";
import QuestionItem from "~/components/questionItem";
import { createEvaluation } from "~/api/evaluation";
import medalData from "~/helper/medal.json";
import { getLessonUserList } from "~/api/user";
export default {
  components: {
    QuestionItem
  },

  data: () => ({
    lang,
    medalData,
    menu: false,
    requiredText: false,
    baseUrl: window.Laravel.base_url,
    contactInfo: [],
    contactInfoItems: [],
    initialCnt: 4,
    selMedalList: [],
    isCreating: false,
    isSuccessed: false,
    isLoading: false,
    noData: false
  }),

  computed: {
    currentPath() {
      return this.$route;
    }
  },

  async created() {
    this.isLoading = true;
    await getLessonUserList({ lessonId: this.currentPath.params.lessonId })
      .then(res => {
        this.contactInfoItems = res.data;
        if (this.contactInfoItems.length == 0) {
          this.noData = true;
        }
      })
      .catch(err => {
        console.log(err.response);
      });
    this.isLoading = false;
  },

  methods: {
    selectedLesson(val) {
      //console.log(val)
    },
    loadContentData(data) {
      if (data.text === "") {
        this.requiredText = true;
        this.votingData.content = [];
        return;
      }
      this.votingData.content.push(data);
    },
    addContent() {
      this.initialCnt++;
    },

    async publishVotingData() {
      let answerData = {
        userList: [],
        selMedalList: [],
        schoolId: null,
        classId: null
        // viewList:[],
        // postShow:[],
      };
      if (this.contactInfo.length == 0) {
        return this.$snackbar.showMessage({
          content: "选择一个学生",
          color: "error"
        });
      }
      if (this.selMedalList.length == 0) {
        return this.$snackbar.showMessage({
          content: "选择一个认可",
          color: "error"
        });
      }
      answerData.userList = this.contactInfo;
      answerData.selMedalList = this.selMedalList;
      answerData.schoolId = this.currentPath.params.schoolId;
      answerData.classId = this.currentPath.params.lessonId;
      this.isCreating = true;
      console.log("answerData", answerData);
      await createEvaluation(answerData)
        .then(res => {
          this.isCreating = false;
          if (this.$isMobile()) {
            this.$router.push({ name: "home" });
          } else {
            this.$router.push({ name: "classSpace.news" });
          }
        })
        .catch(err => {
          //console.log(err.response)
          this.isCreating = false;
        });

      this.isCreating = false;
    },
    selectMedalItem(medalItem) {
      medalItem.medalFlag = !medalItem.medalFlag;
      this.selMedalList = [];
      for (let i = 0; i < this.medalData.medal.length; i++) {
        for (let j = 0; j < this.medalData.medal[i].menuList.length; j++) {
          if (this.medalData.medal[i].menuList[j].medalFlag == true) {
            this.selMedalList.push(
              this.medalData.medal[i].menuList[j].medalText
            );
          }
        }
      }
    }
  }
};
</script>

